package com.valentine.ishu.utils

import android.app.Notification
import android.app.NotificationChannel
import android.app.NotificationManager
import android.app.PendingIntent
import android.content.BroadcastReceiver
import android.content.Context
import android.content.Intent
import androidx.core.content.ContextCompat
import com.valentine.ishu.MainActivity
import com.valentine.ishu.R
import java.util.Calendar

class AlarmReceiver : BroadcastReceiver() {
    override fun onReceive(context: Context, intent: Intent) {
        val calendar = Calendar.getInstance()
        val month = calendar.get(Calendar.MONTH)
        val day = calendar.get(Calendar.DAY_OF_MONTH)

        val (title, text) = getNotificationContent(month, day)
        showNotification(context,title= title, text =  text)

    }

    private fun getNotificationContent(month: Int, day: Int): Pair<String, String> {
        return when {
            month == Calendar.FEBRUARY && day == 1 -> "Happy Sunday Day" to "This is a special notification"
            month == Calendar.FEBRUARY && day == 2 -> "Happy Monday Day" to "This is a Test notification"
            month == Calendar.FEBRUARY && day == 3 -> "Happy Tuesday Day" to "This is a special notification"
            month == Calendar.FEBRUARY && day == 4 -> "Happy Wednesday Day" to "This is a special notification"
            month == Calendar.FEBRUARY && day == 5 -> "Happy Thursday Day" to "This is a special notification"
            month == Calendar.FEBRUARY && day == 6 -> "Happy Friday Day" to "This is a special notification"
            else -> "Holiday" to "This is a regular notification"
        }
    }

    //show notification
    private fun showNotification(context: Context, title: String, text: String) {
        val notificationManager = ContextCompat.getSystemService(
            context,
            NotificationManager::class.java
        ) as NotificationManager

        getNotificationChannel(context)
        notificationManager.notify(1, notification(context,title= title, text=text))
    }

    fun getPendingIntent(context: Context): PendingIntent {
        val intent = Intent(context, MainActivity::class.java)
        return PendingIntent.getActivity(context, 0, intent, PendingIntent.FLAG_IMMUTABLE)
    }

    fun getNotificationChannel(context: Context): NotificationChannel {
        val channel = NotificationChannel("ID", "Ishu", NotificationManager.IMPORTANCE_HIGH)
        val notificationManager = ContextCompat.getSystemService(
            context,
            NotificationManager::class.java
        ) as NotificationManager
        notificationManager.createNotificationChannel(channel)
        return channel
    }

    fun notification(context: Context,title: String, text: String): Notification {
        val notification = Notification.Builder(context, "ID")
            .setContentText(text)
            .setContentTitle(title)
            .setSmallIcon(R.drawable.ic_launcher_foreground)
            .setContentIntent(getPendingIntent(context))
            .build()
        return notification

    }

}

