import com.piasy.kmp.socketio.SocketIO
import com.piasy.kmp.socketio.SocketIOOptions

class SocketManager {
    private val socket = SocketIO.create(
        "https://your-socket-server.com",
        SocketIOOptions()
    )

    fun connect() {
        socket.on("message") { args ->
            println("Received: ${args.first()}")
        }
        socket.connect()
    }

    fun emit(event: String, data: String) {
        socket.emit(event, data)
    }

    fun disconnect() {
        socket.disconnect()
    }
}
